<form action="<?php echo $path('/install') ?>" method="post" class="form-horizontal">
    <div class="panel-body">
        <div id="pre-install-msg">
            <?php echo $trans('pre_install_msg') ?>
        </div>
        <div id="wait-msg" style="display: none">
            <?php echo $trans('install_wait_msg') ?>
            <hr>
        </div>

        <button type="button" id="show-details" class="btn btn-warning" style="display: none">
            <?php echo $trans('install_details') ?>
        </button>

        <div id="log-container" style="display: none">
            <hr>
            <pre id="log-content"
                 style="max-height: 300px; overflow: auto"
                 data-url="<?php echo $_SERVER['SCRIPT_NAME'] ?>">

            </pre>
        </div>
        <div class="sendData">
            <hr>
            <p><?php echo $trans('confirm_send_data'); ?></p>
            <ul>
                <li><?php echo $trans('technical'); ?> (<i><?php echo $trans('technical_data'); ?></i>).
                <li><?php echo $trans('statistics'); ?> (<i><?php echo $trans('statistics_data'); ?></i>).
            </ul>
            <small>
                <p><?php echo $trans('agreeing'); ?>.</p>
                <p><?php echo $trans('personal_data'); ?>.</p>
            </small>
            <div class="radio">
                <label>
                    <input type="radio" name="sendData" value="true" checked="checked">
                    <?php echo $trans('send'); ?>
                </label>
            </div>
            <div class="radio">
                <label>
                    <input type="radio" name="sendData" value="false">
                    <?php echo $trans('not_send'); ?>
                </label>
            </div>
        </div>
    </div>
    <div class="panel-footer">
        <div class="btn-group">
            <a id="pre-install" href="<?php echo $path('/mailing') ?>" class="btn btn-default">
                <?php echo $trans('previous_step') ?>
            </a>
            <button id="do-install" type="submit" class="btn btn-primary">
                <?php echo $trans('do_install') ?>
            </button>
        </div>
    </div>
</form>

<script>
    $(function () {
        $('body').on('click', '#do-install', function () {
            $('#pre-install, #pre-install-msg, .sendData').hide();
            $('#wait-msg, #show-details').css('display', 'block');
        })
        .on('change', '.sendData .radio input', function () {
            $('#do-install').removeAttr('disabled');
        });

        var areDetailsShowed = false;

        $('#show-details').on('click', function () {
            if (areDetailsShowed) {
                return;
            }

            areDetailsShowed = true;
            $('#log-container').css('display', 'block');
            var logElement = $('#log-content');
            var statusUrl = logElement.attr('data-url') + '/status';
            var inlineLoadingImg = '<img style="display: block; margin: 30px 60px" src="data:image/png;base64,"/>';
            var lastLogContent = '';
            var logTimestamp = null;
            var pollStatus = function (url) {
                $.ajax(url).done(function (data) {
                    logTimestamp = data.timestamp;

                    if (data.content !== lastLogContent) {
                        logElement.html(data.content + inlineLoadingImg);
                        logElement.animate({ scrollTop: logElement[0].scrollHeight });
                        lastLogContent = data.content;
                    }
                });
            };
            pollStatus(statusUrl);
            setInterval(function () {
                pollStatus(statusUrl + '/' + logTimestamp);
            }, 2000);
        });
    });
</script>
